    @patch("httpx.get")
    def test_should_return_the_correct_info_with_the_expected_markup(self, get_mock):
        get_mock.return_value = SimpleNamespace(is_success=True, status_code=200, content=self.markup)

        received = retrieve_movie_from_web(self.url)

        plot = "With Spider-Man's identity now revealed, Peter asks Doctor Strange for help. When a spell goes wrong, dangerous foes from other worlds start to appear, forcing Peter to discover what it truly means to be Spider-Man."
        expected = RetrievedMovie(
            title="Spider-Man: No Way Home",
            release_year=2021,
            duration=timedelta(hours=2, minutes=28),
            rating=8.5,
            plot=plot,
            directors=["Jon Watts"],
            genres=["Action", "Adventure", "Fantasy", "Sci-Fi"]
        )

        self.assertEqual(received, expected)


    # Tests for parse_duration
    hours, minutes = 23, 59

    def test_should_parse_duration_return_parsed_value_with_hours_and_minutes(self):
        received = parse_duration(f"{self.hours}h {self.minutes}m")
        expected = timedelta(hours=self.hours, minutes=self.minutes)

        self.assertEqual(received, expected)

    def test_should_parse_duration_return_parsed_value_with_hours(self):
        received = parse_duration(f"{self.hours}h")
        expected = timedelta(hours=self.hours)

        self.assertEqual(received, expected)

    def test_should_parse_duration_return_parsed_value_with_minutes(self):
        received = parse_duration(f"{self.minutes}m")
        expected = timedelta(minutes=self.minutes)

        self.assertEqual(received, expected)

